	public boolean stepOutBoundIntgProcess(String message) throws ErrorHandlingFrameworkException, Exception {

		String transType = null;
		ehfICD = ErrorHandlingFrameworkHandler.getErrorHandlingFrameworkICDFactory(SPRINGBATCH_ECO_SYSTEM, SPRINGBATCH_COMPONENT_ID,
				SPRINGBATCH_ICD_CLASSNAME);

		ehfICD.setAttributePublishId(PUBLISH_ID_NEW);// FIXME
		traceId = ((ErrorHandlingFrameworkICD) ehfICD).createNewTraceId();

		String logMessage = null;
		ehfICD.setAttributeTransactionType(EHF_ATTR_TRANSACTION_TYPE_QUEUETOSPRING);
		logMessage = ehfHandler.getInfoLog(new Date(), traceId, EHF_ERROR_PATH4, EHF_MSGTYPE_INFO_NONSLA,
				"Step outbound message received message from queue", EHF_SPRINGBATCH_ITEMUTILITY_USER, DatamigrationCommonUtil
						.getMethodName(), DatamigrationCommonUtil.getClassName(), ehfICD);
		ehfLogger.info(logMessage);

		try {

			// 1. Parse the Step.xml
			STEPProductInformation stepProdInfo = getStepProductInformation(message);

			if (isValidStepXML(stepProdInfo)) {

				logMessage = ehfHandler.getInfoLog(new Date(), traceId, EHF_ERROR_PATH5, EHF_MSGTYPE_INFO_NONSLA,
						"Retrieve the xPath values from PIMCore message...", EHF_SPRINGBATCH_ITEMUTILITY_USER, DatamigrationCommonUtil
								.getMethodName(), DatamigrationCommonUtil.getClassName(), ehfICD);
				ehfLogger.info(logMessage);

				SheetListVO sheetListVO = null;

				Date date = new Date();
				NumberFormat formatter = new DecimalFormat("#000");
				String dateStr = IntgSrvUtils.getStringFromDate(date, VICTOR_OUTFILE_DATE_FORMAT) + formatter.format(date.getTime() % 1000);
				String outDir = IntgSrvUtils.reformatFilePath(IntgSrvPropertiesReader
						.getProperty(IntgSrvAppConstants.CNP_OUTBOUND_EXCEL_OUTPUT_DIRECTORY));
				File file = new File(outDir);
				if (!file.exists()) {
					file.mkdirs();
				}

				// 2. Check the transaction type
				// 3. create SheetVO Based on the transaction type
				// 3.1 If Web hierarchy use SpreadSheetGenerationWeb.java
				if (message.contains("SPRINGBATCH-REFERENCE-WEB HIERARCHY")) {
					transType = "WEB_HIERARCHY";
					logger.info("WEB HIERARCHY REQUEST");
					logMessage = ehfHandler.getInfoLog(new Date(), traceId, EHF_ERROR_PATH3, EHF_MSGTYPE_INFO_NONSLA,
							"Received WEB HIERARCHY request, retrieving the relevant details from StepProdInformation Object..",
							EHF_SPRINGBATCH_ITEMUTILITY_USER, DatamigrationCommonUtil.getMethodName(), DatamigrationCommonUtil
									.getClassName(), ehfICD);
					ehfLogger.info(logMessage);

					sheetListVO = spreadSheetGenWeb.getWebHierarchyList(stepProdInfo);
					if (sheetListVO != null) {
						logger.info("Final sheetListVO object constructed");
						stepOutboundWriter
								.createExcel(sheetListVO, outDir + "/PIM_Web_Hierarchy_" + dateStr + ".xls", WORK_SHEET_NAMES_WEB);

						logMessage = ehfHandler.getInfoLog(new Date(), traceId, EHF_ERROR_PATH3, EHF_MSGTYPE_INFO_NONSLA,
								"WEB HIERARCHY Spread sheet created.", EHF_SPRINGBATCH_ITEMUTILITY_USER, DatamigrationCommonUtil
										.getMethodName(), DatamigrationCommonUtil.getClassName(), ehfICD);
						ehfLogger.info(logMessage);
					} else {
						logger
								.info("Sheet list vo is null.. Received message may not have sufficient information or issues in parsing. please check the error log..");
						DatamigrationCommonUtil
								.printConsole("Sheet list vo is null.. Received message may not have sufficient information or issues in parsing. please check the error log..");
						logMessage = ehfHandler.getErrorLog(new Date(), traceId, EHF_ERROR_PATH2, new Exception(
								"Received message may not have sufficient information or issues in parsing. please check the error log.."),
								EHF_SPRINGBATCH_ITEMUTILITY_USER, DatamigrationCommonUtil.getMethodName(), DatamigrationCommonUtil
										.getClassName(), ehfICD);
						ehfLogger.error(logMessage);
					}

				}
				// 3.2 If Product Template use SpreadSheetGenerationProduct.java
				else if (message.contains("SPRINGBATCH-REFERENCE-PRODUCT TEMPLATE")) {

					transType = "PRODUCT_TEMPLATE";
					logger.info("TEMPLATE REQUEST");
					logMessage = ehfHandler.getInfoLog(new Date(), traceId, EHF_ERROR_PATH3, EHF_MSGTYPE_INFO_NONSLA,
							"Received PRODUCT TEMPLATE request, retrieving the relevant details from StepProdInformation Object..",
							EHF_SPRINGBATCH_ITEMUTILITY_USER, DatamigrationCommonUtil.getMethodName(), DatamigrationCommonUtil
									.getClassName(), ehfICD);
					ehfLogger.info(logMessage);

					sheetListVO = spreadSheetGenProd.getProductTemplateList(stepProdInfo);
					if (sheetListVO != null) {

						stepOutboundWriter.createExcel(sheetListVO, outDir + "/PIM_Product_Template_" + dateStr + ".xls",
								WORK_SHEET_NAMES_PROD);

						logMessage = ehfHandler.getInfoLog(new Date(), traceId, EHF_ERROR_PATH3, EHF_MSGTYPE_INFO_NONSLA,
								"PRODUCT TEMPLATE Spread sheet created.", EHF_SPRINGBATCH_ITEMUTILITY_USER, DatamigrationCommonUtil
										.getMethodName(), DatamigrationCommonUtil.getClassName(), ehfICD);
						ehfLogger.info(logMessage);
					} else {
						logger
								.info("Sheet list vo is null.. Received message may not have sufficient information or issues in parsing. please check the error log..");
						DatamigrationCommonUtil
								.printConsole("Sheet list vo is null.. Received message may not have sufficient information or issues in parsing. please check the error log..");
						logMessage = ehfHandler.getErrorLog(new Date(), traceId, EHF_ERROR_PATH2, new Exception(
								"Received message may not have sufficient information or issues in parsing. please check the error log.."),
								EHF_SPRINGBATCH_ITEMUTILITY_USER, DatamigrationCommonUtil.getMethodName(), DatamigrationCommonUtil
										.getClassName(), ehfICD);
						ehfLogger.error(logMessage);
					}
				}
				// 3.3. If Configuration hierarchy use
				// SpreadSheetGenerationConfig.java
				else if (message.contains("SPRINGBATCH-REFERENCE-MATERIAL") || message.contains("SPRINGBATCH-REFERENCE-COMPONENT TYPE")
						|| message.contains("SPRINGBATCH-REFERENCE-COMPONENT CATEGORY")) {

					transType = "CONFIG_HIERARCHY";
					logger.info("CONFIG HIERARCHY REQUEST");
					logMessage = ehfHandler
							.getInfoLog(
									new Date(),
									traceId,
									EHF_ERROR_PATH3,
									EHF_MSGTYPE_INFO_NONSLA,
									"Received CONFIG HIERARCHY - MATERIAL/COMPONENT TYPE/COMPONENT CATEGORY request, retrieving the relevant details from StepProdInformation Object..",
									EHF_SPRINGBATCH_ITEMUTILITY_USER, DatamigrationCommonUtil.getMethodName(), DatamigrationCommonUtil
											.getClassName(), ehfICD);
					ehfLogger.info(logMessage);

					sheetListVO = spreadSheetGencConfig.getConfigHierarchyList(stepProdInfo);
					if (sheetListVO != null) {

						stepOutboundWriter.createExcel(sheetListVO, outDir + "/PIM_Config_Hierarchy_" + dateStr + ".xls",
								WORK_SHEET_NAMES_CONFIG);

						logMessage = ehfHandler.getInfoLog(new Date(), traceId, EHF_ERROR_PATH3, EHF_MSGTYPE_INFO_NONSLA,
								"CONFIG HIERARCHY Spread sheet created.", EHF_SPRINGBATCH_ITEMUTILITY_USER, DatamigrationCommonUtil
										.getMethodName(), DatamigrationCommonUtil.getClassName(), ehfICD);
						ehfLogger.info(logMessage);
					} else {
						logger
								.info("Sheet list vo is null.. Received message may not have sufficient information or issues in parsing. please check the error log..");
						DatamigrationCommonUtil
								.printConsole("Sheet list vo is null.. Received message may not have sufficient information or issues in parsing. please check the error log..");
						logMessage = ehfHandler.getErrorLog(new Date(), traceId, EHF_ERROR_PATH2, new Exception(
								"Received message may not have sufficient information or issues in parsing. please check the error log.."),
								EHF_SPRINGBATCH_ITEMUTILITY_USER, DatamigrationCommonUtil.getMethodName(), DatamigrationCommonUtil
										.getClassName(), ehfICD);
						ehfLogger.error(logMessage);
					}
				} else {
					logMessage = ehfHandler.getInfoLog(new Date(), traceId, EHF_ERROR_PATH3, EHF_MSGTYPE_INFO_NONSLA,
							"Step XML doesn't have expected SPRINGBATCH-REFERENCE. Please check the info log..",
							EHF_SPRINGBATCH_ITEMUTILITY_USER, DatamigrationCommonUtil.getMethodName(), DatamigrationCommonUtil
									.getClassName(), ehfICD);
					ehfLogger.info(logMessage);
					logger.info("Step XML doesn't have expected SPRINGBATCH-REFERENCE. Please see below step xml.\n" + message);
					DatamigrationCommonUtil
							.printConsole("Step XML doesn't have expected SPRINGBATCH-REFERENCE. Please see below step xml.\n" + message);
				}

				if (TRUE_STR.equalsIgnoreCase(IntgSrvPropertiesReader.getProperty(DEBUG_XML_GENERATE_FLAG))) {

					debugFileWrite(message, transType, "Step");
				}

			} else {
				logger
						.info("Step XML is invalid.. Received message may not have sufficient information. please check the step XML in log..");
				DatamigrationCommonUtil
						.printConsole("Step XML is invalid.. Received message may not have sufficient information. please check the step XML in log..");
				logMessage = ehfHandler.getErrorLog(new Date(), traceId, EHF_ERROR_PATH2, new Exception(
						"Step XML is invalid.. Received message may not have sufficient information. please check the step XML in log.."),
						EHF_SPRINGBATCH_ITEMUTILITY_USER, DatamigrationCommonUtil.getMethodName(), DatamigrationCommonUtil.getClassName(),
						ehfICD);
				ehfLogger.error(logMessage);
			}
			// 5. Logger
			// 6. Error handling framework
			// 7. Acknowledgment
			// 8. Comments
		} catch (Exception e) {
			DatamigrationCommonUtil.printConsole("Exception e....");
			logMessage = ehfHandler.getErrorLog(new Date(), traceId, EHF_ERROR_PATH2, e, EHF_SPRINGBATCH_ITEMUTILITY_USER,
					DatamigrationCommonUtil.getMethodName(), DatamigrationCommonUtil.getClassName(), ehfICD);
			ehfLogger.error(logMessage);
			logger.error(e);
			logger.info("Message will be rollback....");
			logMessage = ehfHandler.getInfoLog(new Date(), traceId, EHF_ERROR_PATH3, EHF_MSGTYPE_INFO_NONSLA,
					"Message will be rollback....", EHF_SPRINGBATCH_ITEMUTILITY_USER, DatamigrationCommonUtil.getMethodName(),
					DatamigrationCommonUtil.getClassName(), ehfICD);
			ehfLogger.info(logMessage);
			e.printStackTrace();
			// throw new Exception(e);
			return Boolean.FALSE;

		}
		return Boolean.TRUE;
	}

	public static void debugFileWrite(String message, String transType, String systemName) {

		String path = IntgSrvUtils.reformatFilePath(IntgSrvPropertiesReader
				.getProperty(IntgSrvAppConstants.CNP_OUTBOUND_XML_INPUT_DIRECTORY));
		DatamigrationCommonUtil.writerFile(path + systemName + "-" + transType + "-" + new Date().getTime() + ".xml", message);
		logger.info("STEP xml file generated for debug. "+path + systemName + "-" + transType + "-" + new Date().getTime() + ".xml");
	}

	private boolean isValidStepXML(STEPProductInformation stepProdInfo) {

		if (stepProdInfo == null || (stepProdInfo.getClassifications() == null && stepProdInfo.getProducts() == null)) {
			return Boolean.FALSE;
		}
		return Boolean.TRUE;
	}

	private STEPProductInformation getStepProductInformation(String message) {

		STEPProductInformation product = null;
		try {
			/*
			 * JAXBContext jaxbContext = JAXBContext
			 * .newInstance("com.staples.pcm.stepcontract.beans");
			 */
			JAXBContext jaxbContext = DatamigrationCommonUtil.getStepContractJAXBCtxInstance();
			Unmarshaller jaxbUnmarshaller = jaxbContext.createUnmarshaller();
			product = (STEPProductInformation) jaxbUnmarshaller.unmarshal(new InputSource(new StringReader(message)));

		} catch (Exception e) {
			logger.error(e);
			e.printStackTrace();
		}
		return product;
	}

	public static Map<String, String> getAssetMetaDataValues(AssetCrossReferenceType assetCrossRefType) {

		Map<String, String> metaDataMap = new HashMap<String, String>();
		List<Object> assetList = assetCrossRefType.getMetaDataOrAsset();

		for (Object values : assetList) {
			if (METADATA_TYPE.equals(values.getClass().getSimpleName())) {
				MetaDataType met = (MetaDataType) values;
				List<Object> assetList1 = met.getValueOrMultiValueOrValueGroup();
				for (Object val : assetList1) {
					if (VALUE_TYPE.equals(val.getClass().getSimpleName())) {
						ValueType valueTypeObj = (ValueType) val;
						metaDataMap.put(valueTypeObj.getAttributeID(), valueTypeObj.getContent());
					}
				}
			}
		}
		return metaDataMap;
	}

